import React, { useEffect, useState } from 'react';
import { Outlet } from 'react-router-dom';

function Dashboard({ username }) {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        setIsVisible(true);
    }, []);

    return (
        <div className={`flex flex-col items-center justify-center px-4 py-20 ${isVisible ? 'opacity-100' : 'opacity-0'} transition-opacity duration-1000`}>
            <h2 className="text-4xl font-mono text-center mb-8 text-purple-800">
                Welcome to Your Kitchen, Chef {username}!
            </h2>
            <p className="text-lg font-poppins text-center mt-4 text-gray-700">
                Start exploring delicious recipes from around the world and unleash your inner chef!
            </p>
            <Outlet />
        </div>
    );
}

export default Dashboard;
