import React from 'react';
import background from './background.jpg';

const Home = () => {
  return (
    <div className="hero-section flex items-center justify-center min-h-screen" 
         style={{
           backgroundImage: `url(${background})`,
           backgroundSize: 'cover',
           backgroundPosition: 'center',
           color: 'white',
           padding: '0',
           margin: '0',
           boxSizing: 'border-box',
         }}>
      <div className="hero-content text-center">
        <h1 className="text-[52px] font-semibold mb-8 leading-normal italic text-white">
          Cooking Made Easy{' '}
          <span className="text-[52px] font-semibold mb-8 leading-normal text-rose-950">
            One Recipe at a Time
          </span>
        </h1>
        <p className="text-lg lg:text-xl leading-relaxed">
          From savory main courses to delectable desserts, our recipe collection has something for everyone.
          Whether you're planning a cozy family dinner, a romantic date night, or a festive gathering with friends, we've got you covered.
          Get ready to unleash your culinary creativity and delight your taste buds with our irresistible dishes!
        </p>
      </div>
    </div>
  );
};

export default Home;
